import * as React from "react";
import Box from "@mui/material/Box";
import CircularProgress from "@mui/material/CircularProgress";

interface ListProps {
	color: string;
	size: number;
}

const List: React.FC<ListProps> = ({ color = "#54ADEC", size = 40 }) => {
	return (
		<Box
			sx={{
				display: "flex",
				justifyContent: "center",
				alignItems: "center",
			}}
		>
			<CircularProgress
				size={size}
				data-testid="loader"
				sx={{ color: color }}
			/>
		</Box>
	);
};

export default List;
